/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
/**
 * @class ReferenceQualifierFactory
 * @description Reference qualifier factory
 * @since 18/03/2024
 * @date  18/03/2024
 * @version 1.0.0
 * @author Apurv Deshpande E-Mail: <apurv.deshpande@t-systems.com>; Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ReferenceQualifierFactory {

    /**
     * @description Returns the dynamic reference qualifier implementation
     *
     * @returns {String} query to be used as a reference qualifier
     */
    static getReferenceQualifier() {
        return new DynamicReferenceQualifier();
    }

    /**
     * @description Returns the record producer reference qualifier implementation
     *
     * @returns {String} query to be used as a reference qualifier
     */
    static getProducerReferenceQualifier() {
        return new ProducerReferenceQualifier();
    }
    /**
     * @description Returns the configurable  reference qualifier implementation
     *
     * @returns {String} query to be used as a reference qualifier
     */
    static getConfigurableRefQualifier () {
        return new ConfigurableRefQualifier();       
    }
}
