/**
 * @class QueryBuilder
 * @description Helper script to create encoded queries
 * @since 07/06/2024
 * @date  07/06/2024
 * @version 1.0.0
 * @author Apurv Deshpande E-Mail: <apurv.deshpande@t-systems.com>
 *
 */
class QueryBuilder {

	/**
     * @description Function create an encoded query basen on the given paramters.
     *
     * @param {String} user SysID of current user
	 * @param {String} table name of the table
     *
     * @returns {Array} array of queries
     */
    static findRoles(user) {
        let builder = new x_984628_core_sm_n.StringBuilder();

        builder.append(`user=${ user}`);

        return builder.toString();
    }
}
